CREATE TABLE `categories` (`id` int NOT NULL AUTO_INCREMENT,`name` varchar(255) DEFAULT NULL,PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
CREATE TABLE `events` (`id` int NOT NULL AUTO_INCREMENT,`capacity` int DEFAULT NULL,`created_at` datetime(6) DEFAULT NULL,`date` datetime(6) DEFAULT NULL,`description` text,`image` varchar(255) DEFAULT NULL,`name` varchar(255) DEFAULT NULL,`price` decimal(10,2) DEFAULT NULL,`updated_at` datetime(6) DEFAULT NULL,`venue` varchar(255) DEFAULT NULL,`category_id` int DEFAULT NULL,PRIMARY KEY (`id`),KEY `FK_cat_id` (`category_id`),CONSTRAINT `FK_cat_id` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
CREATE TABLE `users` (`id` int NOT NULL AUTO_INCREMENT,`email` varchar(255) DEFAULT NULL,`first_name` varchar(255) DEFAULT NULL,`last_name` varchar(255) DEFAULT NULL,`password` varchar(255) DEFAULT NULL,`roles` varchar(255) DEFAULT NULL,PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
CREATE TABLE `tickets` (`id` int NOT NULL AUTO_INCREMENT,`created_at` datetime(6) DEFAULT NULL,`event_id` int DEFAULT NULL,`user_id` int DEFAULT NULL,PRIMARY KEY (`id`),KEY `FK_event_id` (`event_id`),KEY `FK_user_id` (`user_id`),CONSTRAINT `FK_event_id` FOREIGN KEY (`event_id`) REFERENCES `events` (`id`),CONSTRAINT `FK_user_id` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
